import React, {useState, useEffect} from 'react';
import './style.css';

const Weather = () => {

  const getWeatherInfo = async ()=>{
    try{
      let api_key=process.env.API_KEY;
      const api_url=`https://api.openweathermap.org/data/2.5/weather?q=SantaClara&units=imperial&appid=${api_key}`
      const fetch_response=await fetch(api_url);
      const json_data = await fetch_response.json();
      console.log(json_data.main);
      console.log("hello");
     
    }catch(error){
      console.log(error);
    }
  } ;
  return (
<>
    <div className='searchbox'>
      <input type='text' name='box' id="myplace" placeholder='Type City Name Here...' autoFocus />
      <button type='submit'></button>    
      <div className='btn'>
        {/*<a href='#'><i class='fa fa-search'></i></a> */}
        <i className='material-icons'>search</i>
      </div>
    </div>

    <div className='cards'>
      <div className='card'>
        <div className='container'>
          <div className='icons'>
          <i className={'wi wi-day-sunny'}></i>
          </div>
          <div className='currentInfo'>
            <div className='currentData'>{new Date().toLocaleString()}</div>
            <div className='currentData'></div>
            <div className='currentData'>Place</div>
            <div className='currentData'>Temperature</div>
          </div>
        </div>
      
        
          <div className='weatherInfo'>
              
            <div className='currentData'>MinTemp </div>
            <div className='currentData'>MaxTemp </div>
            <div className='currentData'>WindSpeed</div>
            <div className='currentData'>Description</div>
          </div>
          <div className='coronaInfo'>
            
            <div className='currentData'>NewCases </div>
            <div className='currentData'>TotalCases </div>
            <div className='currentData'>Recovered</div>
            <div className='currentData'>TotalDeath</div>
             
            </div>
          <div className='liveTraffic'>Traffic</div>

      
      </div>

    </div>
  </>
  )
}

export default Weather;
